app.post('/api/login', async (c) => {
  try {
    // DB ë°”ì¸ë”© í™•ì¸
    if (!c.env.DB) {
      return c.json({ 
        success: false, 
        error: 'DB binding not configured. Please check Cloudflare Pages settings.',
        debug: {
          env_keys: Object.keys(c.env),
          has_db: !!c.env.DB
        }
      }, 500)
    }

    const { email, password } = await c.req.json()
    
    if (!email || !password) {
      return c.json({ success: false, error: 'ì´ë©”ì¼ê³¼ ë¹„ë°€ë²ˆí˜¸ë¥¼ ìž…ë ¥í•´ì£¼ì„¸ìš”.' }, 400)
    }

    // ì‚¬ìš©ìž ì¡°íšŒ (ì‹¤ì œ ë°ì´í„°ë² ì´ìŠ¤ ì»¬ëŸ¼ì— ë§žì¶° ìˆ˜ì •)
    const user = await c.env.DB.prepare(`
      SELECT id, email, name, role, points, academy_name FROM users WHERE email = ? AND password = ?
    `).bind(email, password).first()

    if (!user) {
      return c.json({ success: false, error: 'ì´ë©”ì¼ ë˜ëŠ” ë¹„ë°€ë²ˆí˜¸ê°€ ì¼ì¹˜í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤.' }, 401)
    }

    // ì„¸ì…˜ ìƒì„±
    const sessionId = crypto.randomUUID()
    const expiresAt = new Date(Date.now() + 7 * 24 * 60 * 60 * 1000) // 7ì¼
    
    await c.env.DB.prepare(`
      INSERT INTO sessions (session_id, user_id, expires_at)
      VALUES (?, ?, ?)
    `).bind(sessionId, user.id, expiresAt.toISOString()).run()

    // ì¿ í‚¤ ì„¤ì •
    c.header('Set-Cookie', `session_id=${sessionId}; Path=/; HttpOnly; Secure; SameSite=Lax; Max-Age=${7 * 24 * 60 * 60}`)

    return c.json({ 
      success: true, 
      message: 'ë¡œê·¸ì¸ ì„±ê³µ',
      user: { 
        id: user.id, 
        email: user.email, 
        name: user.name, 
        role: user.role, 
        points: user.points || 0,
        academy_name: user.academy_name || '',
        user_type: 'director' // ê¸°ë³¸ê°’ (ì»´ëŸ¼ ì—†ìŒ)
      }
    })
  } catch (err) {
    console.error('Login error:', err)
    console.error('Login error details:', {
      message: err.message,
      stack: err.stack,
      name: err.name
    })
    return c.json({ 
      success: false, 
      error: 'ë¡œê·¸ì¸ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.',
      debug: {
        error_message: err.message,
        error_name: err.name,
        has_db: !!c.env.DB
      }
    }, 500)
  }
})

