app.get('/api/students/list', async (c) => {
  try {
    const userId = c.req.query('userId')
    const userType = c.req.query('userType')
    const classId = c.req.query('classId')
    
    if (!userId) {
      return c.json({ success: false, error: 'ì‚¬ìš©ìž IDê°€ í•„ìš”í•©ë‹ˆë‹¤.' }, 400)
    }
    
    let query = ''
    let params = []
    
    if (userType === 'teacher') {
      // ì„ ìƒë‹˜ì€ ìžì‹ ì´ ë‹´ë‹¹í•˜ëŠ” ë°˜ì˜ í•™ìƒë§Œ ì¡°íšŒ
      if (classId) {
        query = `
          SELECT s.*, c.name as class_name
          FROM students s
          LEFT JOIN classes c ON s.class_id = c.id
          WHERE c.teacher_id = ? AND s.class_id = ? AND s.status = 'active' AND s.id NOT IN (4)
          ORDER BY s.name
        `
        params = [userId, classId]
      } else {
        query = `
          SELECT s.*, c.name as class_name
          FROM students s
          LEFT JOIN classes c ON s.class_id = c.id
          WHERE c.teacher_id = ? AND s.status = 'active' AND s.id NOT IN (4)
          ORDER BY c.name, s.name
        `
        params = [userId]
      }
    } else {
      // ì›ìž¥ë‹˜ì€ ìžì‹ ì˜ í•™ì›ì˜ ëª¨ë“  í•™ìƒ ì¡°íšŒ
      if (classId) {
        query = `
          SELECT s.*, c.name as class_name, u.name as teacher_name
          FROM students s
          LEFT JOIN classes c ON s.class_id = c.id
          LEFT JOIN users u ON c.teacher_id = u.id
          WHERE s.academy_id = ? AND s.class_id = ? AND s.status = 'active'
          ORDER BY s.name
        `
        params = [userId, classId]
      } else {
        query = `
          SELECT s.*, c.name as class_name, u.name as teacher_name
          FROM students s
          LEFT JOIN classes c ON s.class_id = c.id
          LEFT JOIN users u ON c.teacher_id = u.id
          WHERE s.academy_id = ? AND s.status = 'active'
          ORDER BY c.name, s.name
        `
        params = [userId]
      }
    }
    
    const students = await c.env.DB.prepare(query).bind(...params).all()
    
    return c.json({ success: true, students: students.results || [] })
  } catch (error) {
    console.error('Get students error:', error)
    return c.json({ success: false, error: 'í•™ìƒ ëª©ë¡ ì¡°íšŒ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.' }, 500)
  }
})

