app.get('/api/admin/get-user-classes', async (c) => {
  try {
    const { DB } = c.env
    const email = c.req.query('email')
    
    if (!email) {
      return c.json({ success: false, error: 'Email parameter required' }, 400)
    }
    
    // ì‚¬ìš©ìž ì°¾ê¸°
    const user = await DB.prepare('SELECT id, email, name, academy_name FROM users WHERE email = ?')
      .bind(email)
      .first()
    
    if (!user) {
      return c.json({ success: false, error: 'User not found' }, 404)
    }
    
    // í•´ë‹¹ ì‚¬ìš©ìžì˜ ë°˜ ëª©ë¡ (academy_id ì‚¬ìš©)
    const classes = await DB.prepare(`
      SELECT c.*, COUNT(s.id) as student_count
      FROM classes c
      LEFT JOIN students s ON c.id = s.class_id AND s.status = 'active'
      WHERE c.academy_id = ?
      GROUP BY c.id
      ORDER BY c.class_name
    `).bind(user.id).all()
    
    return c.json({
      success: true,
      user,
      classes: classes.results || []
    })
  } catch (error: any) {
    return c.json({ success: false, error: error.message }, 500)
  }
})

