    await c.env.DB.prepare(`
      UPDATE user_permissions 
      SET is_active = 0 
      WHERE user_id = ?
    `).bind(userId).run()

    // 2. ÏÉàÎ°úÏö¥ Í∂åÌïú Ï∂îÍ∞Ä
