    console.error('Update permissions error:', err)
    return c.json({ success: false, error: 'Í∂åÌïú ÏóÖÎç∞Ïù¥Ìä∏ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ' + err.message }, 500)
