app.get('/api/students/:studentId/attendance', async (c) => {
  try {
    const studentId = c.req.param('studentId')
    const startDate = c.req.query('startDate') // YYYY-MM-DD ÌòïÏãù
    const endDate = c.req.query('endDate') // YYYY-MM-DD ÌòïÏãù
    
    if (!c.env.DB) {
      return c.json({ success: false, error: 'DB Ïó∞Í≤∞ Ïã§Ìå®' }, 500)
    }
    
    if (!studentId || !startDate || !endDate) {
      return c.json({ 
        success: false, 
        error: 'ÌïôÏÉù ID, ÏãúÏûëÏùº, Ï¢ÖÎ£åÏùºÏù¥ ÌïÑÏöîÌï©ÎãàÎã§.' 
      }, 400)
    }
    
    console.log('üìÖ [GetAttendance] Student:', studentId, 'Period:', startDate, '-', endDate)
    
    // Ìï¥Îãπ Í∏∞Í∞ÑÏùò Ï∂úÏÑù Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå
    const query = `
      SELECT 
        attendance_date,
        status
      FROM attendance
      WHERE student_id = ?
        AND attendance_date >= ?
        AND attendance_date <= ?
      ORDER BY attendance_date ASC
    `
    
    const result = await c.env.DB.prepare(query)
      .bind(studentId, startDate, endDate)
      .all()
    
    const attendanceRecords = result.results || []
    
    // Ï∂úÏÑù ÌÜµÍ≥Ñ Í≥ÑÏÇ∞
    let attendedDays = 0
    let absentDays = 0
    let lateDays = 0
    let earlyLeaveDays = 0
    
    attendanceRecords.forEach(record => {
      if (record.status === 'Ï∂úÏÑù') attendedDays++
      else if (record.status === 'Í≤∞ÏÑù') absentDays++
      else if (record.status === 'ÏßÄÍ∞Å') lateDays++
      else if (record.status === 'Ï°∞Ìá¥') earlyLeaveDays++
    })
    
    const totalDays = attendanceRecords.length
    const attendanceRate = totalDays > 0 
      ? Math.round((attendedDays / totalDays) * 100) 
      : 0
    
    console.log('‚úÖ [GetAttendance] Found', totalDays, 'records')
    console.log('   Ï∂úÏÑù:', attendedDays, 'Í≤∞ÏÑù:', absentDays, 'ÏßÄÍ∞Å:', lateDays)
    
    return c.json({
      success: true,
      data: {
        records: attendanceRecords,
        summary: {
          totalDays,
          attendedDays,
          absentDays,
          lateDays,
          earlyLeaveDays,
          attendanceRate
        }
      }
    })
    
  } catch (error) {
    console.error('‚ùå [GetAttendance] Error:', error)
    return c.json({ 
      success: false, 
      error: 'Ï∂úÏÑù Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå Ïã§Ìå®: ' + error.message 
    }, 500)
  }
})

// üìä ÌïôÏÉù ÌïôÏäµ ÏÑ±Í≥º Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå API (Ïù¥Ìï¥ÎèÑ, Ï∞∏Ïó¨ÎèÑ)
app.get('/api/students/:studentId/performance', async (c) => {
  try {
    const studentId = c.req.param('studentId')
    const startDate = c.req.query('startDate') // YYYY-MM-DD ÌòïÏãù
    const endDate = c.req.query('endDate') // YYYY-MM-DD ÌòïÏãù
    
    if (!c.env.DB) {
      return c.json({ success: false, error: 'DB Ïó∞Í≤∞ Ïã§Ìå®' }, 500)
    }
    
    if (!studentId || !startDate || !endDate) {
      return c.json({ 
        success: false, 
        error: 'ÌïôÏÉù ID, ÏãúÏûëÏùº, Ï¢ÖÎ£åÏùºÏù¥ ÌïÑÏöîÌï©ÎãàÎã§.' 
      }, 400)
    }
    
    console.log('üìä [GetPerformance] Student:', studentId, 'Period:', startDate, '-', endDate)
    
    // daily_recordsÏóêÏÑú Ïù¥Ìï¥ÎèÑÏôÄ Ï∞∏Ïó¨ÎèÑ Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå
    const query = `
      SELECT 
        record_date,
        attendance,
        understanding_level,
        participation_level,
        homework_status,
        achievement,
        memo
      FROM daily_records
      WHERE student_id = ?
        AND record_date >= ?
        AND record_date <= ?
      ORDER BY record_date ASC
    `
    
    const result = await c.env.DB.prepare(query)
      .bind(studentId, startDate, endDate)
      .all()
    
    const records = result.results || []
    
    // ÌÜµÍ≥Ñ Í≥ÑÏÇ∞
    let totalUnderstanding = 0
    let totalParticipation = 0
    let countUnderstanding = 0
    let countParticipation = 0
    let homeworkCompleted = 0
    let homeworkTotal = 0
    
    records.forEach(record => {
      if (record.understanding_level) {
        totalUnderstanding += record.understanding_level
        countUnderstanding++
      }
      if (record.participation_level) {
        totalParticipation += record.participation_level
        countParticipation++
      }
      if (record.homework_status) {
        homeworkTotal++
        if (record.homework_status === 'ÏôÑÎ£å') homeworkCompleted++
      }
    })
    
    const avgUnderstanding = countUnderstanding > 0 
      ? (totalUnderstanding / countUnderstanding).toFixed(1)
      : 0
    
    const avgParticipation = countParticipation > 0 
      ? (totalParticipation / countParticipation).toFixed(1)
      : 0
    
    const homeworkRate = homeworkTotal > 0
      ? Math.round((homeworkCompleted / homeworkTotal) * 100)
      : 0
    
    console.log('‚úÖ [GetPerformance] Found', records.length, 'records')
    console.log('   ÌèâÍ∑† Ïù¥Ìï¥ÎèÑ:', avgUnderstanding, 'ÌèâÍ∑† Ï∞∏Ïó¨ÎèÑ:', avgParticipation)
    
    return c.json({
      success: true,
      data: {
        records,
        summary: {
          avgUnderstanding: parseFloat(avgUnderstanding),
          avgParticipation: parseFloat(avgParticipation),
          homeworkRate,
          totalRecords: records.length
        }
      }
    })
    
  } catch (error) {
    console.error('‚ùå [GetPerformance] Error:', error)
    return c.json({ 
      success: false, 
      error: 'ÏÑ±Í≥º Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå Ïã§Ìå®: ' + error.message 
    }, 500)
  }
})

