// ÌöåÏõê ÌîÑÎ°úÌïÑ Ï°∞Ìöå API
app.get('/api/user/profile', async (c) => {
  try {
    // ÏÑ∏ÏÖò ÎòêÎäî Ìó§ÎçîÏóêÏÑú ÏÇ¨Ïö©Ïûê ID Í∞ÄÏ†∏Ïò§Í∏∞
    let userId = c.req.header('X-User-Id');
    
    // Ìó§ÎçîÏóê ÏóÜÏúºÎ©¥ ÏÑ∏ÏÖòÏóêÏÑú ÌôïÏù∏
    if (!userId) {
      try {
        const cookies = c.req.header('Cookie');
        if (cookies) {
          const sessionIdMatch = cookies.split(';').find(c => c.trim().startsWith('session_id='));
          if (sessionIdMatch) {
            const sessionId = sessionIdMatch.split('=')[1];
            const session = await c.env.DB.prepare('SELECT user_id FROM sessions WHERE id = ?').bind(sessionId).first();
            if (session) {
              userId = session.user_id.toString();
            }
          }
        }
      } catch (cookieErr) {
        console.error('Error parsing cookies:', cookieErr);
      }
    }
    
    if (!userId) {
      return c.json({ success: false, error: 'Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.' }, 401)
    }

    const user = await c.env.DB.prepare(`
      SELECT id, email, name, phone, academy_name, role, created_at, 
             user_type, academy_id, parent_user_id, points
      FROM users WHERE id = ?
    `).bind(userId).first()

    if (!user) {
      return c.json({ success: false, error: 'ÏÇ¨Ïö©ÏûêÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.' }, 404)
    }

    return c.json({ success: true, user })
  } catch (err) {
    console.error('Get profile error:', err);
    console.error('Error stack:', err.stack);
    return c.json({ success: false, error: 'ÌîÑÎ°úÌïÑ Ï°∞Ìöå Ïã§Ìå®', details: err.message }, 500)
  }
})

