app.post('/api/login', async (c) => {
  try {
    const { email, password } = await c.req.json()
    const { env } = c
    
    // ÏÇ¨Ïö©Ïûê Ï°∞Ìöå
    const user = await env.DB.prepare('SELECT * FROM users WHERE email = ?').bind(email).first()
    
    if (!user) {
      return c.json({ success: false, error: 'Ïù¥Î©îÏùº ÎòêÎäî ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§' }, 401)
    }
    
    // ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏ (Ïã§Ï†úÎ°úÎäî Ìï¥Ïãú ÎπÑÍµêÎ•º Ìï¥Ïïº ÌïòÏßÄÎßå, ÌòÑÏû¨Îäî Îã®Ïàú ÎπÑÍµê)
    if (user.password !== password) {
      return c.json({ success: false, error: 'Ïù¥Î©îÏùº ÎòêÎäî ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§' }, 401)
    }
    
    // ÎπÑÎ∞ÄÎ≤àÌò∏ Ï†úÏô∏ÌïòÍ≥† ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î∞òÌôò
    const userInfo: any = {
      id: user.id,
      email: user.email,
      name: user.name,
      phone: user.phone,
      academy_id: user.academy_id,
      academy_name: user.academy_name,
      role: user.role,
      user_type: user.user_type || user.role, // user_type Ïö∞ÏÑ†, ÏóÜÏúºÎ©¥ role ÏÇ¨Ïö©
      parent_user_id: user.parent_user_id || null
    }
    
    // ‚úÖ STEP 1: ÏõêÏû•ÎãòÏù∏Îç∞ academy_idÍ∞Ä ÏóÜÏúºÎ©¥ user.idÎ°ú ÏÑ§Ï†ïÌïòÍ≥† DB ÏóÖÎç∞Ïù¥Ìä∏
    const isDirector = (user.user_type === 'director' || user.role === 'director' || !user.user_type || user.role === 'user');
    const isTeacher = (user.user_type === 'teacher' || user.role === 'teacher');
    
    if (isDirector && !userInfo.academy_id) {
      console.log('üîß [Login] Director without academy_id, setting to user.id:', user.id)
      userInfo.academy_id = user.id
      
      try {
        await env.DB.prepare(
          'UPDATE users SET academy_id = ? WHERE id = ?'
        ).bind(user.id, user.id).run()
        console.log('‚úÖ [Login] Updated director academy_id in DB:', user.id)
      } catch (err) {
        console.error('Failed to update director academy_id:', err)
      }
    }
    
    // ‚úÖ STEP 2: ÏÑ†ÏÉùÎãòÏù∏Îç∞ academy_idÍ∞Ä ÏóÜÏúºÎ©¥ ÏõêÏû•ÎãòÏóêÍ≤åÏÑú Í∞ÄÏ†∏Ïò§Í∏∞
    if (isTeacher && !userInfo.academy_id && user.parent_user_id) {
      try {
        const director = await env.DB.prepare(
          'SELECT id, academy_id FROM users WHERE id = ?'
        ).bind(user.parent_user_id).first()
        
        if (director) {
          // ÏõêÏû•ÎãòÏùò academy_id ÎòêÎäî ÏõêÏû•ÎãòÏùò id ÏÇ¨Ïö©
          const directorAcademyId = director.academy_id || director.id
          userInfo.academy_id = directorAcademyId
          console.log('üîß [Login] Set teacher academy_id from director:', directorAcademyId)
          
          // ÏÑ†ÏÉùÎãò DB ÏóÖÎç∞Ïù¥Ìä∏
          await env.DB.prepare(
            'UPDATE users SET academy_id = ? WHERE id = ?'
          ).bind(directorAcademyId, user.id).run()
          console.log('‚úÖ [Login] Updated teacher academy_id in DB')
          
          // ÏõêÏû•ÎãòÎèÑ academy_idÍ∞Ä ÏóÜÏóàÎã§Î©¥ ÏõêÏû•Îãò DBÎèÑ ÏóÖÎç∞Ïù¥Ìä∏
          if (!director.academy_id) {
            await env.DB.prepare(
              'UPDATE users SET academy_id = ? WHERE id = ?'
            ).bind(director.id, director.id).run()
            console.log('‚úÖ [Login] Also updated director academy_id in DB:', director.id)
          }
        }
      } catch (err) {
        console.error('Failed to fetch director academy_id:', err)
      }
    }
    
    console.log('üîê [Login] Final user info:', {
      id: userInfo.id,
      academy_id: userInfo.academy_id,
      user_type: userInfo.user_type,
      role: userInfo.role
    })
    
    // ÏÑ†ÏÉùÎãòÏù∏ Í≤ΩÏö∞ Í∂åÌïú Ï†ïÎ≥¥ Ï°∞Ìöå
    if (isTeacher) {
      try {
        const permData = await env.DB.prepare(`
          SELECT permissions 
          FROM teacher_permissions 
          WHERE teacher_id = ?
        `).bind(user.id).first()
        
        if (permData && permData.permissions) {
          userInfo.permissions = JSON.parse(permData.permissions)
          console.log('Teacher permissions loaded:', userInfo.permissions)
        } else {
          // Í∏∞Î≥∏ Í∂åÌïú ÏÑ§Ï†ï
          userInfo.permissions = {
            canViewAllStudents: false,
            canWriteDailyReports: false,
            assignedClasses: []
          }
          console.log('No permissions found, using defaults')
        }
      } catch (permErr) {
        console.error('Error loading teacher permissions:', permErr)
        // Í∂åÌïú Ï°∞Ìöå Ïã§Ìå® Ïãú Í∏∞Î≥∏ Í∂åÌïú
        userInfo.permissions = {
          canViewAllStudents: false,
          canWriteDailyReports: false,
          assignedClasses: []
        }
      }
    }
    
    return c.json({ success: true, message: 'Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ', user: userInfo })
  } catch (err) {
    console.error('Login error:', err)
    return c.json({ success: false, error: 'Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§' }, 500)
  }
})

