      { route: '/tools/dashboard-analytics', name: 'ÌÜµÌï© Î∂ÑÏÑù ÎåÄÏãúÎ≥¥Îìú' },
      { route: '/tools/search-volume', name: 'ÎÑ§Ïù¥Î≤Ñ Í≤ÄÏÉâÎüâ Ï°∞Ìöå' }
