// üõí Ïù∏Ïä§ÌÉÄÍ∑∏Îû® ÏÉÅÌíà Íµ¨Îß§ API
app.post('/api/store/purchase-instagram', async (c) => {
  try {
    const sessionId = getCookie(c, 'session_id')
    if (!sessionId) {
      return c.json({ success: false, error: 'Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§' }, 401)
    }

    const DB = c.env.DB
    
    // ÏÑ∏ÏÖòÏóêÏÑú ÏÇ¨Ïö©Ïûê Ï°∞Ìöå
    const session = await DB.prepare(`
      SELECT user_id FROM sessions WHERE session_id = ?
    `).bind(sessionId).first()

    if (!session) {
      return c.json({ success: false, error: 'Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ ÏÑ∏ÏÖòÏûÖÎãàÎã§' }, 401)
    }

    const userId = session.user_id

    // ÏöîÏ≤≠ Îç∞Ïù¥ÌÑ∞
    const { productKey, optionName, price, quantity, targetUrl, apiKey } = await c.req.json()

    if (!productKey || !optionName || !price || !quantity || !targetUrl) {
      return c.json({ success: false, error: 'ÌïÑÏàò Ï†ïÎ≥¥Í∞Ä ÎàÑÎùΩÎêòÏóàÏäµÎãàÎã§' }, 400)
    }

    // Ï¥ù ÎπÑÏö© Í≥ÑÏÇ∞
    const totalCost = price * quantity

    // ÏÇ¨Ïö©Ïûê Ìè¨Ïù∏Ìä∏ Ï°∞Ìöå
    const user = await DB.prepare(`
      SELECT balance FROM users WHERE id = ?
    `).bind(userId).first()

    if (!user) {
      return c.json({ success: false, error: 'ÏÇ¨Ïö©ÏûêÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§' }, 404)
    }

    if (user.balance < totalCost) {
      return c.json({ 
        success: false, 
        error: `Ìè¨Ïù∏Ìä∏Í∞Ä Î∂ÄÏ°±Ìï©ÎãàÎã§. (Î≥¥Ïú†: ${user.balance.toLocaleString()}Ïõê, ÌïÑÏöî: ${totalCost.toLocaleString()}Ïõê)` 
      }, 400)
    }

    // Ìè¨Ïù∏Ìä∏ Ï∞®Í∞ê
    const newBalance = user.balance - totalCost
    await DB.prepare(`
      UPDATE users SET balance = ? WHERE id = ?
    `).bind(newBalance, userId).run()

    // Ï£ºÎ¨∏ ÏÉùÏÑ±
    const productName = `Ïù∏Ïä§ÌÉÄÍ∑∏Îû® ${productKey} - ${optionName}`
    const orderInsert = await DB.prepare(`
      INSERT INTO store_orders (
        user_id, product_name, product_key, option_name, 
        quantity, unit_price, total_price, target_url,
        status, created_at
      ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, 'pending', CURRENT_TIMESTAMP)
    `).bind(
      userId, productName, productKey, optionName,
      quantity, price, totalCost, targetUrl
    ).run()

    const orderId = orderInsert.meta.last_row_id

    // Ìè¨Ïù∏Ìä∏ Í±∞Îûò Í∏∞Î°ù
    const balanceBefore = user.balance
    const balanceAfter = newBalance
    await DB.prepare(`
      INSERT INTO point_transactions (
        user_id, amount, balance_before, balance_after,
        transaction_type, description, created_at
      ) VALUES (?, ?, ?, ?, 'purchase', ?, CURRENT_TIMESTAMP)
    `).bind(
      userId, -totalCost, balanceBefore, balanceAfter,
      `Ïä§ÌÜ†Ïñ¥ Íµ¨Îß§: ${productName}`
    ).run()

    console.log(`[Store] Instagram purchase - User: ${userId}, Product: ${productName}, Quantity: ${quantity}, Cost: ${totalCost}`)

    return c.json({
      success: true,
      message: 'Íµ¨Îß§Í∞Ä ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§',
      orderId,
      remainingPoints: newBalance
    })

  } catch (err: any) {
    console.error('[Store] Purchase error:', err)
    return c.json({ success: false, error: 'Íµ¨Îß§ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ' + err.message }, 500)
  }
})

