                        localStorage.removeItem('user');
                localStorage.removeItem('loginTime');
                        alert('ÏÑ∏ÏÖòÏù¥ ÎßåÎ£åÎêòÏóàÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
                        shouldRedirect = true;
