app.post('/api/store/purchase', async (c) => {
  try {
    const sessionId = getCookie(c, 'session_id')
    if (!sessionId) {
      return c.json({ success: false, error: 'Not authenticated' }, 401)
    }

    const session = await c.env.DB.prepare(`
      SELECT user_id FROM sessions WHERE session_id = ? AND expires_at > datetime('now')
    `).bind(sessionId).first()
    
    if (!session) {
      return c.json({ success: false, error: 'Session expired' }, 401)
    }

    const userId = session.user_id
    const { productId, quantity, targetUrl } = await c.req.json()

    if (!productId || !quantity || quantity <= 0) {
      return c.json({ success: false, error: 'ÏûòÎ™ªÎêú ÏöîÏ≤≠ÏûÖÎãàÎã§.' }, 400)
    }

    // ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå
    const user = await c.env.DB.prepare(`
      SELECT id, email, points FROM users WHERE id = ?
    `).bind(userId).first()

    if (!user) {
      return c.json({ success: false, error: 'User not found' }, 404)
    }

    // ÏÉÅÌíà Ï†ïÎ≥¥ Ï°∞Ìöå
    const product = await c.env.DB.prepare(`
      SELECT * FROM store_products WHERE id = ? AND enabled = 1
    `).bind(productId).first()

    if (!product) {
      return c.json({ success: false, error: 'ÏÉÅÌíàÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.' }, 404)
    }

    const totalCost = product.price * quantity
    const currentPoints = user.points || 0

    // Ìè¨Ïù∏Ìä∏ Î∂ÄÏ°± ÌôïÏù∏
    if (currentPoints < totalCost) {
      return c.json({ 
        success: false, 
        error: 'Ìè¨Ïù∏Ìä∏Í∞Ä Î∂ÄÏ°±Ìï©ÎãàÎã§.',
        required: totalCost,
        current: currentPoints,
        shortage: totalCost - currentPoints
      }, 400)
    }

    // Ìä∏ÎûúÏû≠ÏÖò ÏãúÏûë
    const newPoints = currentPoints - totalCost

    // Ìè¨Ïù∏Ìä∏ Ï∞®Í∞ê
    await c.env.DB.prepare(`
      UPDATE users SET points = ? WHERE id = ?
    `).bind(newPoints, userId).run()

    // Ìè¨Ïù∏Ìä∏ Í±∞Îûò ÎÇ¥Ïó≠ Ï∂îÍ∞Ä
    await c.env.DB.prepare(`
      INSERT INTO point_transactions (
        user_id, type, amount, balance_after, description, created_at
      ) VALUES (?, 'purchase', ?, ?, ?, datetime('now'))
    `).bind(userId, -totalCost, newPoints, `${product.name} x${quantity} Íµ¨Îß§`).run()

    // Ï£ºÎ¨∏ ÏÉùÏÑ±
    const orderResult = await c.env.DB.prepare(`
      INSERT INTO store_orders (
        user_id, product_id, product_name, quantity, 
        price_per_unit, total_price, target_url, 
        status, created_at
      ) VALUES (?, ?, ?, ?, ?, ?, ?, 'pending', datetime('now'))
    `).bind(
      userId, 
      productId, 
      product.name, 
      quantity, 
      product.price, 
      totalCost, 
      targetUrl || null
    ).run()

    const orderId = orderResult.meta.last_row_id

    return c.json({
      success: true,
      message: 'Íµ¨Îß§Í∞Ä ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.',
      order: {
        id: orderId,
        product: product.name,
        quantity,
        totalCost,
        remainingPoints: newPoints
      }
    })
  } catch (error) {
    console.error('[Store Purchase] Error:', error)
    return c.json({ success: false, error: error.message }, 500)
  }
})

